const int BT_WRITE_TASK_ID = 3;
const int BT_OUT_QUEUE_ID = 0;
const int BT_IN_QUEUE_ID = 1;
const int BT_OUT_SEND_WAIT = 3;

const int MAIN_TASK_PRIO = 0;
const int MOTOR_TASK_PRIO = 1;
