int wcet[N_TASKS] = {1, 1, 1, 1};
int bcet[N_TASKS] = {1, 1, 1, 1};
int cyc_wcet[N_HANDLERS] = {1, 1, 1, 1};
int cyc_bcet[N_HANDLERS] = {1, 1, 1, 1};
int handler_queue[N_HANDLERS] = {-1, -1, -1, -1};
int bt_in_queue[BT_IN_QUEUE_SIZE][2] = {{-1, -1}};
int send_wait_q[BT_OUT_SEND_WAIT] = {NULL, NULL, NULL};
int[-1, N_TASKS] g_snd_tsk = NULL;
