clock border_c;

typedef struct 
{
    int id; 
    int prio;
} task_info;

typedef struct 
{
    int id; 
    int period;
    int activation;
} handler_info;


/**
 * Returns the first free position in task queue.
 */
int find_free_position_task_queue() {
    int pos;
    for (pos = 0; pos < N_TASKS; pos++) {
        if (tasks.queue[pos].prio == QUEUE_DEFAULT_PRIORITY
        && tasks.queue[pos].id == QUEUE_DEFAULT_IDENTIFIER) {
            return pos;
        }
    }
    return ERR_QUEUE_MAX_ITEMS;
}

/**
 * Returns the first free position in handler queue.
 */
int find_free_position_handler_queue() {
    int pos;
    for (pos = 0; pos < N_HANDLERS; pos++) {
        if (handler_queue[pos] == NULL) {
            return pos;
        }
    }
    return ERR_QUEUE_MAX_ITEMS;
}

/**
 * Sorts task queue.
 */
void sort_task_queue() {
    int i, prio, j, id, waiting;
    for (i = 1; i < N_TASKS; i++) {
        prio = tasks.queue[i].prio;
        id = tasks.queue[i].id;
        waiting = tasks.queue[i].waiting;
        j = i - 1;

        /* Move elements of arr[0..i-1], that are
          greater than key, to one position ahead
          of their current position */
        while (j >= 0 && ((tasks.queue[j].prio > prio && waiting == false && id != NULL)
                         || (tasks.queue[j].prio > prio && tasks.queue[j].waiting && waiting == true && id != NULL)
                         || (tasks.queue[j].prio < prio && tasks.queue[j].waiting && waiting == false && id != NULL))) {
            tasks.queue[j + 1].waiting = tasks.queue[j].waiting;
            tasks.queue[j + 1].prio = tasks.queue[j].prio;
            tasks.queue[j + 1].id = tasks.queue[j].id;
            j = j - 1;
        }

        tasks.queue[j + 1].id = id;
        tasks.queue[j + 1].prio = prio;
        tasks.queue[j + 1].waiting = waiting;
    }
}

/**
 * Add parameters to task queue and sort afterwards.
 */
void add_task(int identifier, int task_prio) {
    int pos = find_free_position_task_queue();
    tasks.queue[pos].id = identifier;
    tasks.queue[pos].prio = task_prio;
    tasks.queue[pos].waiting = QUEUE_DEFAULT_WAITING;
    sort_task_queue();
}

/**
 * Add parameters to handler queue.
 */
void add_handler(int identifier) {
    int pos = find_free_position_handler_queue();
    handler_queue[pos] = identifier;
}

/**
 * Removes handler from queue when finished execution.
 */
void remove_handler() {
    int pos;
    handler_queue[0] = NULL;
    for (pos = 0; pos < (N_HANDLERS - 1); pos++){
        if (handler_queue[pos] == NULL && handler_queue[pos + 1] != NULL) {
            handler_queue[pos] = handler_queue[pos + 1];
            handler_queue[pos + 1] = NULL;
        }
    }
}

/**
 * Sets waiting property for given identifier.
 */
void set_waiting(int id, bool value) {
    int pos;
    for (pos = 0; pos < N_TASKS; pos++) {
        if (tasks.queue[pos].id == id) {
            tasks.queue[pos].waiting = value;
        }
    }
    sort_task_queue();
}

/**
 * Returns next task's id.
 */
int next_task_id() {
    return tasks.queue[0].id;
}

/**
 * Returns next handler's id.
 */
int next_handler_id() {
    return handler_queue[0];
}

/**
 * Removes task with given identifier from task queue
 */
void remove_task(int id) {
    int pos;
    for (pos = 0; pos < N_TASKS; pos++) {
        if (tasks.queue[pos].id == id) {
            tasks.queue[pos].id = QUEUE_DEFAULT_IDENTIFIER;
            tasks.queue[pos].prio = QUEUE_DEFAULT_PRIORITY;
            tasks.queue[pos].waiting = true;
        }
    }
    sort_task_queue();
}

/**
 * Finds a free position in bt_in_q
 */
int find_free_position_bt_in_q() {
    int pos;
    for (pos = 0; pos < BT_IN_QUEUE_SIZE; pos++) {
        if (bt_in_queue[pos][0] == -1) {
            return pos;
        }
    }
    return ERR_QUEUE_MAX_ITEMS;
}

/**
 * Add parameters to task queue and sort afterwards
 */
void add_bt_in_q(int type, int value) {
    int pos = find_free_position_bt_in_q();
    if (pos == ERR_QUEUE_MAX_ITEMS) {
        return;
    }
    bt_in_queue[pos][0] = type;
    bt_in_queue[pos][1] = value;
}

/**
 * True if bt_in_queue is empty.
 */
bool is_bt_in_q_empty() {
    return (bt_in_queue[0][0] == -1);
}

/**
 * Move elements to first positions.
 */
void sort_bt_in_queue() {
    int i;
    for (i = 0; i < BT_IN_QUEUE_SIZE - 1; i++) {
        if (bt_in_queue[i][0] == -1 && bt_in_queue[i + 1][0] != -1) {
            bt_in_queue[i][0] = bt_in_queue[i + 1][0];
            bt_in_queue[i][1] = bt_in_queue[i + 1][1];
            bt_in_queue[i + 1][0] = NULL;
            bt_in_queue[i + 1][1] = NULL;
        }
    }
}

/**
 * Process first element of bt_in_q
 */
void process_bt_in_q() {
    if (bt_in_queue[0][0] != NULL) {
        bt_in_queue[0][0] = NULL;
        bt_in_queue[0][1] = NULL;
    }
    sort_bt_in_queue();
}

/**
 * Returns next msg type.
 */
int next_msg_type() {
    if (bt_in_queue[0][0] != NULL) {
        return bt_in_queue[0][0];
    }
    return NULL;
}

/**
 * Returns next msg value.
 */
int next_msg_val() {
    if (bt_in_queue[0][0] != NULL) {
        int value = bt_in_queue[0][1];
        bt_in_queue[0][0] = NULL;
        bt_in_queue[0][1] = NULL;
        sort_bt_in_queue();
        return value;
    }
    return NULL;
}

/*
 * Generates a packet with type and value. (used in BTMessageGenerator)
 */
void set_packet(int type, int value) {
    packet[0][0] = type;
    packet[0][1] = value;
}

/*
 * Adds packet to Bluetooth q
 */
void add_packet_to_q() {
    add_bt_in_q(packet[0][0], packet[0][1]);
    packet[0][0] = NULL;
    packet[0][1] = NULL;
}

/*
 * Returns true if any task is ready,
 * false otherwise
 */
bool task_ready() {
    return !tasks.queue[0].waiting;
}

/**
 * Returns true if at least one handler is ready.
 */
bool handler_ready() {
    return handler_queue[0] != NULL;
}

/**
 * Returns the first free position in handler queue.
 */
int find_free_position_send_wait_queue() {
    int pos;
    for (pos = 0; pos < BT_OUT_SEND_WAIT; pos++) {
        if (send_wait_q[pos] == NULL) {
            return pos;
        }
    }
    return ERR_QUEUE_MAX_ITEMS;
}

/**
 * Adds sending task to send-wait queue.
 */
void add_send_wait() {
    int pos = find_free_position_send_wait_queue();
    send_wait_q[pos] = g_snd_tsk;
}

/**
 * Removes first entry from send-wait queue and moves other entries forward by one position.
 */
void remove_send_wait() {
    int i;
    send_wait_q[0] = NULL;
    for (i = 0; i < BT_OUT_SEND_WAIT - 1; i++) {
        if (send_wait_q[i] == NULL && send_wait_q[i + 1] != NULL) {
            send_wait_q[i] = send_wait_q[i + 1];
            send_wait_q[i + 1] = NULL;
        }
    }
}

/**
 * Returns true if send_wait_q is empty. Otherwise false.
 */
bool send_wait_empty() {
    return send_wait_q[0] == NULL;
}

nta.system:
typedef int[0,N_TASKS-1] task_range;
typedef int[0,N_HANDLERS-1] handler_range;
const task_info task_infos[N_TASKS] = {{MAIN_TASK_ID, MAIN_TASK_PRIO}, {MOTOR_TASK_ID, MOTOR_TASK_PRIO}, {BT_READ_TASK_ID, BT_READ_TASK_PRIO}, {BT_WRITE_TASK_ID, BT_WRITE_TASK_PRIO} };
const handler_info handler_infos[N_HANDLERS] = {{HANDLER_NO_MOVEMENT, 100, 0}, {HANDLER_BORDER, 50, 0}, {HANDLER_IR, 100, 0},  {HANDLER_PIXY, 100, 500} };
// List one or more processes to be composed into a system.
main = Main(MAIN_TASK_ID);
scheduler = Scheduler();

// main task and body
mainTaskBody = MainTaskBody(MAIN_TASK_ID, MOTOR_TASK_ID, BT_READ_TASK_ID, BT_WRITE_TASK_ID);

// motor control task and body
motorControlTaskBody = MotorControlTaskBody(MOTOR_TASK_ID, bit_pattern);

// bt read task and body
btReadTaskBody = BtReadTaskBody(BT_READ_TASK_ID);

// bt write task
btWriteTaskBody = BtWriteTaskBody(BT_WRITE_TASK_ID);

// infrared flag and cyclic handler
infraredHandlerBody = InfraredHandlerBody(HANDLER_IR);
infraredTesterAutomaton = IRGenericTesterTA();

// border flag and cyclic handler
borderHandlerBody = BorderHandlerBody(HANDLER_BORDER);
borderTesterAutomaton = BorderTesterTA();

// no movement cyclic handler
noMovementHandlerBody = NoMovementHandlerBody(HANDLER_NO_MOVEMENT);

// pixy cam cyclic handler
pixyCamHandlerBody = PixyCamHandlerBody(HANDLER_PIXY);
pixyTesterAutomaton = PixyGenericTesterTA();

// BT Message Generator
btMsgGenerator = BTMessageGenerator();

// DATA QUEUES
BtOutQueue = AsyncDataQueue(BT_OUT_QUEUE_ID, BT_WRITE_TASK_ID);
BtInQueue = SyncDataQueue(BT_IN_QUEUE_ID, MAIN_TASK_ID, BT_READ_TASK_ID, 50);

Tasks(const task_range i) = TaskHead(task_infos[i].id, task_infos[i].prio, bcet, wcet);
Handlers(const handler_range i) = CyclicHandlerHead(handler_infos[i].id, handler_infos[i].period, handler_infos[i].activation, cyc_wcet, cyc_bcet);

// declare whole system
system main, scheduler,
Tasks, Handlers,
mainTaskBody, motorControlTaskBody, btReadTaskBody, btWriteTaskBody,
infraredHandlerBody, borderHandlerBody, noMovementHandlerBody, pixyCamHandlerBody, 
// COMMENT THIS OUT FOR THE VERIFICATION OF THE A[] properties
btMsgGenerator, 
BtOutQueue, BtInQueue,
infraredTesterAutomaton,  borderTesterAutomaton, pixyTesterAutomaton
;
